import React from 'react' 
import "../index.css"
import Header from './Header'
import Main from './Main'
import Footer from './Footer'
import { useState, useEffect } from 'react';
import PopupWithForm from './PopupWithForm'

function App() { 

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false); 
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false); 
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false); 

  function handleEditProfileClick() {
    isEditProfilePopupOpen = setIsEditProfilePopupOpen(true); 
  }

  function handleAddPlaceClick() {
    isAddPlacePopupOpen = setIsAddPlacePopupOpen(true); 
  }

  function handleEditAvatarClick() {
    isEditAvatarPopupOpen = setIsEditAvatarPopupOpen(true); 
  }

  return (
  <div className ="root">
   <Header />
   <Main 
   onEditProfile={handleEditProfileClick}
   onAddPlace={handleAddPlaceClick}
   onEditAvatar={handleEditAvatarClick}


   />
   <Footer />
   <PopupWithForm
          name="edit"
          title="Редактировать профиль"
          isPopupOpen={handleEditProfileClick}
  ></PopupWithForm>

   <PopupWithForm name='new-card' title='Новое место'/>
   <PopupWithForm name='confirm' title='Обновить аватар' />
   <PopupWithForm name='edit-avatar' title='Вы уверены?' />


  </div>
  ); 
} 

export default App;
