function App() { 

  const[cards, setCards] = useState([]);

  useEffect(() => {
    api.getCards()
      .then(([cards]) => {
        setCards(
            cards
        );
      })
    })

  return (
  <div className ="root">
   <Header />
   <Main 
   cards={cards}
   />
   <Footer />
   <PopupWithForm
          name="edit"
          title="Редактировать профиль"
          isPopupOpen={handleEditProfileClick}
  ></PopupWithForm>

   <PopupWithForm name='new-card' title='Новое место'/>
   <PopupWithForm name='confirm' title='Обновить аватар' />
   <PopupWithForm name='edit-avatar' title='Вы уверены?' />


  </div>
  ); 
} 

export default App;
